import React, { useState } from 'react';
import './section0.css';

const Section0 = () => {
  const [infoList, setInfoList] = useState([]);
  const [info0, setInfo0] = useState('');
  const [info1, setInfo1] = useState('');
  const [info2, setInfo2] = useState('');
  const [editIndex, setEditIndex] = useState(-1);

  const handleInfo0Change = (e) => {
    setInfo0(e.target.value);
  };

  const handleInfo1Change = (e) => {
    setInfo1(e.target.value);
  };

  const handleInfo2Change = (e) => {
    setInfo2(e.target.value);
  };

  const handleSave = () => {
    if (editIndex !== -1) {
      // 수정 모드일 때
      const updatedInfoList = [...infoList];
      updatedInfoList[editIndex] = { info0, info1, info2 };
      setInfoList(updatedInfoList);
    } else {
      // 추가 모드일 때
      setInfoList([...infoList, { info0, info1, info2 }]);
    }
    setInfo0('');
    setInfo1('');
    setInfo2('');
    setEditIndex(-1);
  };

  const handleEdit = (index) => {
    const { info0, info1, info2 } = infoList[index];
    setInfo0(info0);
    setInfo1(info1);
    setInfo2(info2);
    setEditIndex(index);
  };

  const handleDelete = (index) => {
    const updatedInfoList = [...infoList];
    updatedInfoList.splice(index, 1);
    setInfoList(updatedInfoList);
  };

  const getSequenceNumber = (index) => {
    return index + 1;
  };

  return (
    <div>
      <div>
        <input placeholder='Device ID' type="text" value={info0} onChange={handleInfo0Change} />
        <input placeholder='User ID' type="text" value={info1} onChange={handleInfo1Change} />
        <input placeholder='User Name' type="text" value={info2} onChange={handleInfo2Change} />
        <button onClick={handleSave} className="save-button">{editIndex !== -1 ? 'Update' : 'Save'}</button>
      </div>
      <ul className="info-list">
        {infoList.map((info, index) => (
          <li key={index}  className="info-item">
            <p className="sequence-number">Sequence Number: {getSequenceNumber(index)}</p>
            <p className="info">did: {info.info0}</p>
            <p className="info">uid: {info.info1}</p>
            <p className="info">username: {info.info2}</p>
            <div className="button-group">
                <button onClick={() => handleEdit(index)} className="edit-button">Edit</button>
                <button onClick={() => handleDelete(index)} className="delete-button">Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Section0;
