import React, { Component } from 'react';

const NumofMem = ["1", "2", "3", "4", "5", "6", "7", "8", "9"];


class Section0 extends Component {
  state = {
    NumofMem:'',
    did: '',
    uid: '',
    username:''
  }
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }
  handleSubmit = (e) => {
    // 페이지 리로딩 방지
    e.preventDefault();
    // 상태값을 onCreate 를 통하여 부모에게 전달
    this.props.onCreate(this.state);
    // 상태 초기화
    this.setState({
        NumofMem:'',
        did: '',
        uid: '',
        username:''
    })
  }
  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <div className="form-item">
          <h1>2. 사는 곳은 어딘가요?</h1>
          <Select
            // 이름과 마찬가지로 contry값 설정
