import React, { Component } from 'react';

const NumofMem = ["1", "2", "3", "4", "5", "6", "7", "8", "9"];
var userinfo = '';

class Section0 extends Component {
  state = {
    num: '',
    did: '',
    uid: '',
    username:'',
  }
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }
  handleSubmit = (e) => {
    // 페이지 리로딩 방지
    e.preventDefault();
    // 상태값을 onCreate 를 통하여 부모에게 전달
    this.props.onCreate(this.state);
    userinfo += "Device ID: "+this.state.did+"\r"+"User ID"+this.state.uid+"User Name"+this.state.username+"\r\n"
    // 상태 초기화
    this.setState({
        num: '',
        did: '',
        uid: '',
        username:'',
    })
  }
  render() {
    return (
        <div>
        <form onSubmit={this.handleSubmit}>
           <input
            placeholder="num of mem"
            value={this.state.num}
            onChange={this.handleChange}
            name="num"
            // 추가로 셀렉박스에 넘겨줄 리스트를 props에 넣어준다.
          />
            <input
            placeholder="Device ID"
            value={this.state.did}
            onChange={this.handleChange}
            name="did"
            />
            <input
            placeholder="User ID"
            value={this.state.uid}
            onChange={this.handleChange}
            name="uid"
            />
            <input
            placeholder="User Name"
            value={this.state.username}
            onChange={this.handleChange}
            name="username"
            />
            <button>저장</button>
            <div>
                {userinfo}
            </div>
        </form>
        <button
            onClick={() => {
                alert("등록했습니다.");
            }}
        >등록완료</button>
      </div>
    );
  }
}

export default Section0;
