import React, { useState } from 'react';

const InformationForm = () => {
  const [infoList, setInfoList] = useState([]);
  const [info1, setInfo1] = useState('');
  const [info2, setInfo2] = useState('');
  const [editIndex, setEditIndex] = useState(-1);

  const handleInfo1Change = (e) => {
    setInfo1(e.target.value);
  };

  const handleInfo2Change = (e) => {
    setInfo2(e.target.value);
  };

  const handleSave = () => {
    if (editIndex !== -1) {
      // 수정 모드일 때
      const updatedInfoList = [...infoList];
      updatedInfoList[editIndex] = { info1, info2 };
      setInfoList(updatedInfoList);
    } else {
      // 추가 모드일 때
      setInfoList([...infoList, { info1, info2 }]);
    }
    setInfo1('');
    setInfo2('');
    setEditIndex(-1);
  };

  const handleEdit = (index) => {
    const { info1, info2 } = infoList[index];
    setInfo1(info1);
    setInfo2(info2);
    setEditIndex(index);
  };

  const handleDelete = (index) => {
    const updatedInfoList = [...infoList];
    updatedInfoList.splice(index, 1);
    setInfoList(updatedInfoList);
  };

  return (
    <div>
      <div>
        <input type="text" value={info1} onChange={handleInfo1Change} />
        <input type="text" value={info2} onChange={handleInfo2Change} />
        <button onClick={handleSave}>{editIndex !== -1 ? 'Update' : 'Save'}</button>
      </div>
      <ul>
        {infoList.map((info, index) => (
          <li key={index}>
            <p>Info 1: {info.info1}</p>
            <p>Info 2: {info.info2}</p>
            <button onClick={() => handleEdit(index)}>Edit</button>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Section0;
