import React, { Component } from 'react';

const NumofMem = ["1", "2", "3", "4", "5", "6", "7", "8", "9"];
var userinfo = '';

class Section0 extends Component {
  state = {
    did: '',
    uid: '',
    username:'',
  }
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }
  handleSubmit = (e) => {
    // 페이지 리로딩 방지
    e.preventDefault();
    // 상태값을 onCreate 를 통하여 부모에게 전달
    this.props.onCreate(this.state);
    userinfo
    // 상태 초기화
    this.setState({
        did: '',
        uid: '',
        username:'',
    })
  }
  render() {
    return (
        <div>
        <form onSubmit={this.handleSubmit}>
        {this.state.userinfo}
            <input
            placeholder="Device ID"
            value={this.state.did}
            onChange={this.handleChange}
            name="did"
            />
            <input
            placeholder="User ID"
            value={this.state.uid}
            onChange={this.handleChange}
            name="uid"
            />
            <input
            placeholder="User Name"
            value={this.state.username}
            onChange={this.handleChange}
            name="username"
            />
            <button>저장</button>
        </form>
        <div>{this.state.userinfo}</div>
        <button
            onClick={() => {
                alert("등록했습니다.");
            }}
        >등록완료</button>
      </div>
    );
  }
}

export default Section0;
