import React, { useState } from 'react';

const Section0 = () => {
  const [info1, setInfo1] = useState('');
  const [info2, setInfo2] = useState('');
  const [editMode, setEditMode] = useState(false);

  const handleInfo1Change = (e) => {
    setInfo1(e.target.value);
  };

  const handleInfo2Change = (e) => {
    setInfo2(e.target.value);
  };

  const handleSave = () => {
    setEditMode(false);
  };

  const handleEdit = () => {
    setEditMode(true);
  };

  const handleDelete = () => {
    setInfo1('');
    setInfo2('');
  };

  return (
    <div>
      {editMode ? (
        <div>
          <input type="text" value={info1} onChange={handleInfo1Change} />
          <input type="text" value={info2} onChange={handleInfo2Change} />
          <button onClick={handleSave}>Save</button>
          <button onClick={handleDelete}>Delete</button>
        </div>
      ) : (
        <div>
          <p>Info 1: {info1}</p>
          <p>Info 2: {info2}</p>
          <button onClick={handleEdit}>Edit</button>
        </div>
      )}
    </div>
  );
};

export default Section0;
